mapper.select:
SELECT * FROM 
         (SELECT * FROM T_BOARD  WHERE TNUM <= #{param1} 
                   ORDER BY TNUM ASC) A 
         WHERE A.TNUM >= #{param2}

mapper.update:
UPDATE T_BOARD SET USERID=#{param2},TTITLE=#{param3}, 
                    TCONTENT=#{param4}, TDATE=#{param5}
               WHERE TNUM=#{param1}
