package springweb.a03_data.vo;

import java.util.ArrayList;

public class Gantt {
	private ArrayList<Task> tasks;
	private int selectedRow;
	private ArrayList<String> deletedTaskIds;
	private ArrayList<Resource> resources;
	private ArrayList<Role> roles;
	private boolean canWrite;
	private boolean canDelete;
	private boolean canWriteOnParent;
	private boolean canAdd;
	public Gantt() {
		super();
		// TODO Auto-generated constructor stub
	}
	public ArrayList<Task> getTasks() {
		return tasks;
	}
	public void setTasks(ArrayList<Task> tasks) {
		this.tasks = tasks;
	}
	public int getSelectedRow() {
		return selectedRow;
	}
	public void setSelectedRow(int selectedRow) {
		this.selectedRow = selectedRow;
	}
	public ArrayList<String> getDeletedTaskIds() {
		return deletedTaskIds;
	}
	public void setDeletedTaskIds(ArrayList<String> deletedTaskIds) {
		this.deletedTaskIds = deletedTaskIds;
	}
	public ArrayList<Resource> getResources() {
		return resources;
	}
	public void setResources(ArrayList<Resource> resources) {
		this.resources = resources;
	}
	public ArrayList<Role> getRoles() {
		return roles;
	}
	public void setRoles(ArrayList<Role> roles) {
		this.roles = roles;
	}
	public boolean isCanWrite() {
		return canWrite;
	}
	public void setCanWrite(boolean canWrite) {
		this.canWrite = canWrite;
	}
	public boolean isCanDelete() {
		return canDelete;
	}
	public void setCanDelete(boolean canDelete) {
		this.canDelete = canDelete;
	}
	public boolean isCanWriteOnParent() {
		return canWriteOnParent;
	}
	public void setCanWriteOnParent(boolean canWriteOnParent) {
		this.canWriteOnParent = canWriteOnParent;
	}
	public boolean isCanAdd() {
		return canAdd;
	}
	public void setCanAdd(boolean canAdd) {
		this.canAdd = canAdd;
	}
	
	
}
